CREATE TABLE `products` (
  `prod_id` int(11) NOT NULL,
  `img` varchar(255) NOT NULL,
  `img_big` varchar(255) NOT NULL,
  `prod_name` varchar(128) NOT NULL,
  `prod_caption` varchar(255) NOT NULL,
